import React, { Component } from "react";


class CalendlyWidgetInline extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isWidgetVisible: false
        };
    }

    toggleCalendlyWidget = () => {
        this.setState(prevState => ({
            isWidgetVisible: !prevState.isWidgetVisible
        }));
    };

    render() {
        return (
            <div className="container">
                <button onClick={this.toggleCalendlyWidget}>Open Calendly Widget</button>
                {this.state.isWidgetVisible && (
                    <div className="calendly-inline-widget" data-url="https://calendly.com/sairambandi/30min?hide_gdpr_banner=1" style={{ minWidth: "320px", height: "700px" }}></div>
                )}
                {this.state.isWidgetVisible && (
                    <script type="text/javascript" src="https://assets.calendly.com/assets/external/widget.js" async></script>
                )}
            </div>
        );
    }
}

export default CalendlyWidgetInline;
